mapper.select:

		select
            cmntId, cmntTitle, cmntDate, cmntUpdatedate, cmntDepartment, cmntContent, cmntCount, memId
        from (
            select /*+INDEX_DESC(tbl_community pk_community) */
                rownum rn, cmntId, cmntTitle, cmntDate, cmntUpdatedate, cmntDepartment, cmntContent, cmntCount, memId
            from
                tbl_community
            where



        rownum <= #{pageNum} * #{amount}
     )
     where
         rn > (#{pageNum} -1) * #{amount}
