mapper.select:
select cmntId, cmntTitle, cmntContent, memId, cmntDepartment, cmntDate, cmntUpdatedate
from (
select rownum rn, cmntId, cmntTitle, cmntContent, memId, cmntDepartment, cmntDate, cmntUpdatedate
from tbl_community
where cmntId > 0



and rownum <= #{pageNum} * #{amount}
)
where rn > (#{pageNum} -1) * #{amount}
