mapper.select:
select dnewsId, dnewsTitle, dnewsDepartment, dnewsDate, dnewsCount, memId
from (
    select /*+INDEX_DESC(tbl_dongnews SYS_C007080) */
        dnewsId, dnewsTitle, dnewsDepartment, dnewsDate, dnewsCount, memId,
        ROW_NUMBER() over (order by dnewsId desc) as rn
    from tbl_dongnews

)
where rn between (#{pageNum} - 1) * #{amount} + 1 and #{pageNum} * #{amount}

mapper.update:
update tbl_dongnews
set dnewsTitle = #{dnewsTitle}, dnewsDepartment = #{dnewsDepartment}, dnewsTel = #{dnewsTel}, dnewsContent = #{dnewsContent}, dnewsUpdatedate = sysdate
where dnewsId = #{dnewsId}

mapper.select:
select dnewsId, dnewsTitle from ( select dnewsId, dnewsTitle, ROW_NUMBER() over (order by dnewsId desc) as rn from tbl_dongnews where dnewsId < #{dnewsId}
and
dnewsTitle LIKE '%'||#{cri.searchKeyword}||'%'
dnewsContent LIKE '%'||#{cri.searchKeyword}||'%'
) where rn = 1

select dnewsId, dnewsTitle from ( select dnewsId, dnewsTitle, ROW_NUMBER() over (order by dnewsId asc) as rn from tbl_dongnews where dnewsId > #{dnewsId}
and
dnewsTitle LIKE '%'||#{cri.searchKeyword}||'%'
dnewsContent LIKE '%'||#{cri.searchKeyword}||'%'
) where rn = 1
