mapper.select:
select galId, galTitle, galDate, galUpdatedate, galDepartment, galContent, galCount, galTel, memId
from (
select /*+INDEX_DESC(tbl_gallery SYS_C007039) */
galId, galTitle, galDate, galUpdatedate, galDepartment, galContent, galCount, galTel, memId,
ROW_NUMBER() over (order by galId desc) as rn
from tbl_gallery

)
where rn between (#{pageNum} - 1) * #{amount} + 1 and #{pageNum} * #{amount}

mapper.update:
UPDATE tbl_gallery
SET galCount = galCount + 1
WHERE galId = #{galId}

mapper.select:
select galId, galTitle from ( select galId, galTitle, ROW_NUMBER() over (order by galId desc) as rn from tbl_gallery where galId < #{galId}
and
galTitle LIKE '%'||#{cri.searchKeyword}||'%'
galContent LIKE '%'||#{cri.searchKeyword}||'%'
galDepartment LIKE '%'||#{cri.searchKeyword}||'%'
) where rn = 1

select galId, galTitle from ( select galId, galTitle, ROW_NUMBER() over (order by galId asc) as rn from tbl_gallery where galId > #{galId}
and
galTitle LIKE '%'||#{cri.searchKeyword}||'%'
galContent LIKE '%'||#{cri.searchKeyword}||'%'
galDepartment LIKE '%'||#{cri.searchKeyword}||'%'
) where rn = 1


select count(*)
from tbl_gallery
where galDate >= #{startDate} and galDate < #{endDate}
